import React, { useState } from 'react'
import infoIcon from "../../src/assets/images/info-icon.svg"
import { useEffect } from 'react'
import { useRef } from 'react'


export default function Support() {
    const divTechnologieEntreprise = useRef();
    const supportBtnIntro = useRef();
    const divDesigneBrand = useRef();
    const divManagamentStartup = useRef();
    const btnAnim = useRef();
    const firstSpan = useRef();
    const secondSpan = useRef();
    const titreSouligneUn=useRef();
    const titreSouligneDeux=useRef();
    const titreSouligneTrois=useRef();
    const titreSouligneCacheUn=useRef();
    const titreSouligneCacheDeux=useRef();
    const titreSouligneCacheTrois=useRef();
    const affichageInformation=useRef();
    const allTitreSouligne =[titreSouligneUn,titreSouligneDeux,titreSouligneTrois];
    const allTitreSouligneCache =[titreSouligneCacheUn,titreSouligneCacheDeux,titreSouligneCacheTrois];
    const [boolAnim, setboolAnim] = useState(true);
    const [boolInfo, setboolInfo] = useState(true);

    function onclickTalent (){
        if(boolAnim){
            return;
        }
        btnAnim.current.classList.add('animDroiteGaucheContainer2');
        btnAnim.current.classList.remove('animGaucheDroiteContainer2');
        btnAnim.current.textContent="";
        firstSpan.current.style.opacity ="0";
        secondSpan.current.style.opacity ="1";
        setTimeout(() => {
                btnAnim.current.textContent = "The talent";
        }, 200);
        allTitreSouligne.forEach(element => {
            element.current.classList.remove("animTitreSouligneDescend");
            element.current.classList.add("animTitreSouligneMonter");
        });
        allTitreSouligneCache.forEach(element => {
            element.current.classList.remove("animTitreCacheSouligneMonter");
            element.current.classList.add("animTitreSouligneCacheDescend");
        });
        setboolAnim(!boolAnim);
    }

    function onclickClient(){
        if(!boolAnim){
            return;
        }
        btnAnim.current.classList.remove('animDroiteGaucheContainer2');
        btnAnim.current.classList.add('animGaucheDroiteContainer2');
        btnAnim.current.textContent="";
        firstSpan.current.style.opacity ="1";
        secondSpan.current.style.opacity ="0";
         setTimeout(() => {
                btnAnim.current.textContent = "The clients";
        }, 200);
         allTitreSouligne.forEach(element => {
            element.current.classList.remove("animTitreSouligneMonter");
            element.current.classList.add("animTitreSouligneDescend");
        });
        allTitreSouligneCache.forEach(element => {
            element.current.classList.remove("animTitreSouligneCacheDescend");
            element.current.classList.add("animTitreCacheSouligneMonter");
        });
        setboolAnim(!boolAnim);
    }

    function clickImg(){
        if(boolInfo){
            affichageInformation.current.style.opacity="1";
            setboolInfo(!boolInfo);
        }
         if(!boolInfo){
            affichageInformation.current.style.opacity="0";
            setboolInfo(!boolInfo);
        }
    }
    
    useEffect(() => {
        const scrolling=()=>{
            const tailleEcran = window.innerHeight
            const hauteurDivTechno = divTechnologieEntreprise.current.getBoundingClientRect().top;
            if(hauteurDivTechno<=tailleEcran){
                supportBtnIntro.current.style.transform=`translateY(0px)`;
                supportBtnIntro.current.style.opacity=`1`;
                setTimeout(() => {
                    divDesigneBrand.current.style.transform=`translateY(0px)`;
                    divDesigneBrand.current.style.opacity=`1`;
                }, 100);
                setTimeout(() => {
                    divTechnologieEntreprise.current.style.transform=`translateY(0px)`;
                    divTechnologieEntreprise.current.style.opacity=`1`;
                }, 200);
                setTimeout(() => {
                    divManagamentStartup.current.style.transform=`translateY(0px)`;
                    divManagamentStartup.current.style.opacity=`1`;
                }, 300);
            }
        }
        window.addEventListener("scroll", scrolling);
      return () => {
        window.removeEventListener("scroll", scrolling);
      }
    }, )
    

  return (
    <div style={{fontFamily:"Libre Baskerville"}} className='supp456:h-[1500px] w-screen h-[1200px] bg-blanc flex flex-col items-center justify-around '>
        <div className='h-[160px] flex flex-col items-center justify-around  '>
            <p ref={supportBtnIntro} className='supp550:text-3xl text-2xl font-thin transform translate-y-[-150px] opacity-0 transition-all duration-[0.4s] ease-out'>
                Who we support
            </p>
            <div  className='w-[270px] supp550:w-[350px] flex items-center justify-between h-[50px] relative py-[28px] px-[30px] hover:cursor-pointer  border-[3px] border-bleuGris rounded-[50px]  '>
                <p ref={btnAnim} className='absolute top-0 left-0 bg-bleuGris w-[55%] h-full rounded-[50px] text-grisClair flex items-center justify-center'>
                    The talent
                </p>
                <span ref={firstSpan} onClick={onclickTalent} className='text-grisMoyen transition-opacity duration-300'>The talent</span>
                <span ref={secondSpan} onClick={onclickClient} className='text-grisMoyen transition-opacity duration-300'>The clients</span>
            </div>
        </div>
        <div ref={divDesigneBrand} className='relative overflow-hidden transition-all duration-[0.4s] ease-out transform translate-y-[-100px] opacity-0 border-b-[3px] border-b-bleuGris' >
            <p ref={titreSouligneUn} id="para-design" className=' supp550:text-[60px] supp1100:text-[90px] text-[40px] text-bleuGris font-bold hover:cursor-pointer '>
                Design
            </p>
            <p ref={titreSouligneCacheUn} className=' supp550:text-[60px] supp1100:text-[90px] text-[40px] text-bleuGris font-bold hover:cursor-pointer absolute top-0 left-[50%] transform translate-x-[-50%] translate-y-[100%] '>
                Brand
            </p>
        </div>
        <div ref={divTechnologieEntreprise} className='relative overflow-hidden transition-all duration-[0.4s] ease-out transform translate-y-[-100px] opacity-0 border-b-[3px] border-b-bleuGris'>
            <p ref={titreSouligneDeux} className=' supp550:text-[60px] supp1100:text-[90px] text-[40px] text-bleuGris font-bold hover:cursor-pointer '>
                Technologie
            </p>
            <p ref={titreSouligneCacheDeux} className=' supp550:text-[60px] supp1100:text-[90px] text-[40px] text-bleuGris font-bold hover:cursor-pointer absolute top-0 left-[50%] transform translate-x-[-50%] translate-y-[100%] '>
                Entreprises
            </p>
        </div>
        <div ref={divManagamentStartup} className='relative overflow-hidden transition-all duration-[0.4s] ease-out transform translate-y-[-100px] opacity-0 border-b-[3px] border-b-bleuGris'>
            <p ref={titreSouligneTrois} className=' supp550:text-[60px] supp1100:text-[90px] text-[40px] text-bleuGris font-bold hover:cursor-pointer '>
                Management
            </p>
            <p ref={titreSouligneCacheTrois} className=' supp550:text-[60px] supp1100:text-[90px] text-[40px] text-bleuGris font-bold hover:cursor-pointer absolute top-0 left-[50%] transform translate-x-[-50%] translate-y-[100%] '>
                Startup|Tech
            </p>
        </div>

        <div className='relative'>
            <div ref={affichageInformation}  style={{boxShadow:"rgba(0, 0, 0, 0.25) 0px 14px 28px, rgba(0, 0, 0, 0.22) 0px 10px 10px"}} className='supp456:w-[350px] supp456:left-[-350%] supp550:w-[400px] supp550:top-[-400%] supp550:left-[-400%] w-[300px] top-[-500%] left-[-300%] absolute bg-white p-6 text-center text-xl text-grisTexte leading-[34px] rounded-[10px] transform rotate-[-3deg] opacity-0 transition-all duration-300 ease-in-out '>
                say hey, we'll awlays be honnest about what we focus on, and what we dont.
            </div>
            <img onClick={clickImg} className='hover:cursor-pointer' src={infoIcon} alt=" ronde et noire, avec un i au milieu faisant référence à information" />
        </div>
    </div>
  )
}
